def get_address_from_private_key(private_key):
    w3 = Web3()
    account = w3.eth.account.from_key(private_key)
    return account.address

def approveAll(address,privatekey):
    print("\tApprove All Item to Camp Contract")
    print("====================================================================")
    amountOfItems = HeroCatItem.functions.balanceOf(address).call()
    print(f"Approve Items {amountOfItems}")
    for index in range(amountOfItems):
        ItemID = HeroCatItem.functions.tokenOfOwnerByIndex(address,int(index)).call()
        txRaw = HeroCatItem.functions.approve(Camp.address,int(ItemID)).build_transaction({"from": address})
        hashTx = sendTransaction(txRaw, address, privatekey)
        print(f"Approve Item ID : {ItemID} Success.")

def setHero(address, privatekey):
    try:
        print("\tSetting Heroes into Camps")
        print("====================================================================")
        amountOfHero = HeroCat.functions.balanceOf(address).call()
        print(f"Amount of Hero: {amountOfHero}")
        for index in range(amountOfHero):
            # Get the ID of the Hero
            HeroID = HeroCat.functions.tokenOfOwnerByIndex(address, index).call()
            print(f"HeroID: {HeroID}")
        print("====================================================================")
        for index in range(amountOfHero):
            HeroID = HeroCat.functions.tokenOfOwnerByIndex(address, index).call()
            txRaw = HeroCat.functions.approve(Camp.address, HeroID).build_transaction({"from": address})
            hashTx = sendTransaction(txRaw, address, privatekey)
            print(f"Approve HERO ID: {HeroID} Success.")
            txRaw = Camp.functions.equip(HeroID, HeroID).build_transaction({"from": address})
            hashTx = sendTransaction(txRaw, address, privatekey)
            print(f"Send {HeroID} into Camp Success.")
            time.sleep(2)
    except Exception as e:
        print(f"Error occurred: {e}")


def setItem(address,privatekey):
    amountOfHero = Camp.functions.characterOwnerBalanceOf(address).call()
    print(f"\tSetting Item into Hero \tTotal {amountOfHero}")     ### Display All Hero
    print("====================================================================")
    for index in range(amountOfHero):
        HeroID = Camp.functions.characterOfOwnerByIndex(address,int(index)).call()
        HeroPow = Camp.functions.nftEquipPower(address, int(HeroID)).call()
        print(f"HeroID : {HeroID}\t Power: {HeroPow}")
    print("====================================================================")
    selectHero = input("Please Select Hero(ID) Type * to Exit: ")  ## Select Hero to Management
    if selectHero == "*":
        return 0
    else:
        HeroPow = Camp.functions.nftEquipPower(address, int(selectHero)).call()
        print(f"Hero Details\t HEROID : {selectHero} \t Power {HeroPow} \n Eqiupment:")
        heroInfo = Camp.functions.nftEquipItemsInfo(address, int(selectHero)).call()
        len(heroInfo)
        NotitemSlot = []
        for data in range(30):
            if heroInfo[data] != 0:
                notEmpty = int(heroInfo[data] / 1000000000000000000000)
                print(f"Item ID : {heroInfo[data]}\t Slot {notEmpty}")
                NotitemSlot.append(notEmpty)
        print("====================================================================")
        amountOfItems = HeroCatItem.functions.balanceOf(address).call()
        print(f"\tAvailable Items In Your Inventory \tTotal {amountOfItems}")
        for index in range(amountOfItems):
            ItemID = HeroCatItem.functions.tokenOfOwnerByIndex(address, int(index)).call()
            itemSlot = int(ItemID / 1000000000000000000000)
            if itemSlot not in NotitemSlot:
                print(f"Item ID : {ItemID}\t Slot [{itemSlot}] (Eqiupable)")
            else:
                print(f"Item ID : {ItemID}\t Slot [{itemSlot}] (Hero Slot not Empty)")
        print("====================================================================")

        selectItem = input("Please Select Item(ID) Type * to Exit: ")
        if selectItem == "*":
            return 0
        txRaw = Camp.functions.equip(int(selectHero),int(selectItem)).build_transaction({"from": address})
        hashTx = sendTransaction(txRaw, address, privatekey)
        print(f"Eqiup Item {selectItem} into HeroID {selectHero} Success.")

